import React, { useState, useEffect } from "react";
import { Container, Row, Table, Col, Card, Button } from "react-bootstrap";
import { useLocation, Link } from "react-router-dom";


function useQuery() {
    return new URLSearchParams(useLocation().search);
}

export default function ShowUser() {



    const [users, setUsers] = useState(null);

    const [err, setError] = useState(false);

    useEffect(() => {
        fetchedUsers();
    }, []);

    async function fetchedUsers() {
        try {
            const response = await fetch(
                "http://localhost:8080/clients",
                {
                    method: "GET",
                    headers: {
                        'Accept': 'application/json',
                        "Content-Type": "application/json",
                    },
                    redirect: "follow",
                }
            );
            let fetchedUsers = await response.json();
            JSON.parse(JSON.stringify(fetchedUsers))
            setUsers(fetchedUsers)
            // setLoading(false);
            console.log("elo" + users);
        } catch (err) {
            console.log(err);
            setError(err);
        }
    }

    let query = useQuery();

    return (
        <Container fluid>
            <Row className=" mt-5 ">
                <Col>
                    <Card style={{ width: '18rem' }}>
                        <Card.Body>
                            <Card.Title>Dodaj użytkownika</Card.Title>
                            <Button href="/CreateUser" variant="primary">Wybierz</Button>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>

            <Row className="d-flex  pt-5">

                {users && users.map((user, index) => {
                    return (
                        <Card style={{ width: '18rem' }}>
                            <Card.Body>
                                <Card.Title>Użytkownik nr. {user.id}</Card.Title>
                                <Card.Text>
                                    Imię 
                                </Card.Text>
                                <Button variant="primary">Go somewhere</Button>
                            </Card.Body>
                        </Card>
                    )
                })}
            </Row>
        </Container>
    );
};
